import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [godownItems, setGodownItems] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    if (!godownItems[godownId]) {
      axios.get(`http://localhost:5000/api/items?godown_id=${godownId}`)
        .then(response => {
          setItems(response.data);
          setGodownItems(prev => ({ ...prev, [godownId]: response.data }));
        })
        .catch(error => console.error('Error fetching items:', error));
    } else {
      setItems(godownItems[godownId]);
    }
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const renderTree = (parentId) => {
    const children = godowns.filter(godown => godown.parent_godown === parentId);
    if (children.length === 0) return null;

    return (
      <ul className="godown-tree">
        {children.map(child => (
          <li key={child.id}>
            <div className="godown-node" onClick={() => toggleGodown(child.id)}>
              {expandedGodowns[child.id] ? '▼' : '▶'} {child.name}
            </div>
            {expandedGodowns[child.id] && (
              <>
                {renderTree(child.id)}
                {/* Show button only if there are items */}
                {godownItems[child.id] && godownItems[child.id].length > 0 && (
                  <button className="view-items-btn" onClick={() => handleGodownClick(child.id)}>
                    View Items
                  </button>
                )}
              </>
            )}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h2>Godowns</h2>
        {renderTree(null)}
      </div>
      <div className="main">
        <h2>Item Details</h2>
        {items.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          <div className="items-list">
            {items.map(item => (
              <div className="item-card" key={item.item_id}>
                <img src={item.image_url} alt={item.name} className="item-image" />
                <div className="item-details">
                  <h3>{item.name}</h3>
                  <p>Category: {item.category}</p>
                  <p>Price: ${item.price}</p>
                  <p>Brand: {item.brand}</p>
                  <p>Quantity: {item.quantity}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
