// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  GlobalStyle,
  LoginContainer,
  Title,
  Form,
  InputGroup,
  Input,
  InputIcon,
  Button,
  ErrorMessage,
} from './LoginStyles'; // Adjust the import path as necessary
import { User, Lock, AlertCircle } from 'lucide-react';
import axios from 'axios'; // Import axios for making API calls

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5005/api/auth', {
        username: email, // Use the email as username
        password: password,
      });

      if (response.data.message === 'Authentication successful') {
        navigate('/dashboard'); // Redirect to the main app after login
      } else {
        setError('Invalid email or password');
      }
    } catch (error) {
      setError('Authentication failed');
    }
  };

  return (
    <>
      <GlobalStyle />
      <LoginContainer>
        <Title>Welcome Back</Title>
        <Form onSubmit={handleLogin}>
          <InputGroup>
            <InputIcon>
              <User size={20} />
            </InputIcon>
            <Input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </InputGroup>
          <InputGroup>
            <InputIcon>
              <Lock size={20} />
            </InputIcon>
            <Input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </InputGroup>
          <Button type="submit">Login</Button>
        </Form>
        {error && (
          <ErrorMessage>
            <AlertCircle size={16} style={{ marginRight: '5px' }} />
            {error}
          </ErrorMessage>
        )}
      </LoginContainer>
    </>
  );
}

export default Login;
