import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});

  useEffect(() => {
    // Fetch godowns from the backend
    axios.get('http://localhost:5000/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    // Fetch items based on the selected godown
    axios.get(`http://localhost:5000/api/items?godown_id=${godownId}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const renderTree = (parentId) => {
    const children = godowns.filter(godown => godown.parent_godown === parentId);
    if (children.length === 0) return null;

    return (
      <ul>
        {children.map(child => (
          <li key={child.id}>
            <div onClick={() => toggleGodown(child.id)}>
              {child.name}
            </div>
            {expandedGodowns[child.id] && renderTree(child.id)}
            {expandedGodowns[child.id] && (
              <button onClick={() => handleGodownClick(child.id)}>View Items</button>
            )}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="App">
      <div className="sidebar">
        <h2>Godowns</h2>
        {renderTree(null)}
      </div>
      <div className="main">
        <h2>Item Details</h2>
        {items.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          items.map(item => (
            <div key={item.item_id}>
              <h3>{item.name}</h3>
              <p>Category: {item.category}</p>
              <p>Price: ${item.price}</p>
              <p>Brand: {item.brand}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Dashboard;
