import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled, { createGlobalStyle, keyframes } from 'styled-components';
import { ChevronDown, ChevronRight, Package } from 'lucide-react';

const GlobalStyle = createGlobalStyle`
  body {
    background-color: #1a1a2e;
    color: #e0e0e0;
    font-family: 'Inter', sans-serif;
  }
`;

const DashboardContainer = styled.div`
  display: flex;
  height: 100vh;
  overflow: hidden;
`;

const Sidebar = styled.div`
  width: 300px;
  background-color: #16213e;
  padding: 20px;
  overflow-y: auto;
  transition: width 0.3s ease;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #16213e;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #0f3460;
    border-radius: 4px;
  }

  @media (max-width: 768px) {
    width: ${props => (props.isOpen ? '300px' : '0')};
  }
`;

const Main = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #1a1a2e;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #0f3460;
    border-radius: 4px;
  }
`;

const Title = styled.h2`
  color: #e94560;
  margin-bottom: 20px;
`;

const GodownTree = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const GodownNode = styled.div`
  display: flex;
  align-items: center;
  padding: 10px;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #0f3460;
  }
`;

const ViewItemsButton = styled.button`
  background-color: #e94560;
  color: white;
  border: none;
  padding: 5px 10px;
  margin-left: 20px;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #ff6b6b;
  }
`;

const ItemsList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 20px;
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const ItemCard = styled.div`
  background-color: #16213e;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  animation: ${fadeIn} 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
  }
`;

const ItemImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const ItemDetails = styled.div`
  padding: 15px;
`;

const ItemName = styled.h3`
  margin: 0 0 10px;
  color: #e94560;
`;

const ItemInfo = styled.p`
  margin: 5px 0;
  font-size: 14px;
`;

const MenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: #e94560;
  font-size: 24px;
  cursor: pointer;

  @media (max-width: 768px) {
    display: block;
    position: fixed;
    top: 10px;
    left: 10px;
    z-index: 1000;
  }
`;

export default function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5005/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    axios.get(`http://localhost:5005/api/items?godown_id=${godownId}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const renderTree = (parentId) => {
    const children = godowns.filter(godown => godown.parent_godown === parentId);
    if (children.length === 0) return null;

    return (
      <GodownTree>
        {children.map(child => (
          <li key={child.id}>
            <GodownNode onClick={() => toggleGodown(child.id)}>
              {expandedGodowns[child.id] ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
              <span style={{ marginLeft: '5px' }}>{child.name}</span>
            </GodownNode>
            {expandedGodowns[child.id] && (
              <>
                {renderTree(child.id)}
                <ViewItemsButton onClick={() => handleGodownClick(child.id)}>
                  View Items
                </ViewItemsButton>
              </>
            )}
          </li>
        ))}
      </GodownTree>
    );
  };

  return (
    <>
      <GlobalStyle />
      <MenuButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        ☰
      </MenuButton>
      <DashboardContainer>
        <Sidebar isOpen={isSidebarOpen}>
          <Title>Godowns</Title>
          {renderTree(null)}
        </Sidebar>
        <Main>
          <Title>Item Details</Title>
          {items.length === 0 ? (
            <p>No items found for this location</p>
          ) : (
            <ItemsList>
              {items.map(item => (
                <ItemCard key={item.item_id}>
                  <ItemImage src={item.image_url} alt={item.name} />
                  <ItemDetails>
                    <ItemName>{item.name}</ItemName>
                    <ItemInfo>Category: {item.category}</ItemInfo>
                    <ItemInfo>Price: ${item.price}</ItemInfo>
                    <ItemInfo>Brand: {item.brand}</ItemInfo>
                    <ItemInfo>Quantity: {item.quantity}</ItemInfo>
                  </ItemDetails>
                </ItemCard>
              ))}
            </ItemsList>
          )}
        </Main>
      </DashboardContainer>
    </>
  );
}
